mapper.select:
select 

from c_cust_bank
where cust_id = #{custId}
  and bank_code = #{bankCode}

    select 
    ,
    c.real_name,
    c.cert_no
    from c_cust_bank t left join c_cust_info c
      on c.cust_id = t.cust_id
    
    	and t.bank_id = #{bankId}
    	and t.cust_id = #{custId}
    	and bank_code = #{bankCode}
    	
	    	and c.real_name = #{custInfo.realName}
	    	and c.cert_no = #{custInfo.certNo}
    	
    	and bank_info = #{bankInfo}
    	and account_type = #{accountType}
    	and enabled = #{enabled}
    	and already_auth = #{alreadyAuth}
