mapper.select:
  	select 
    ,
    
    from c_cust_info t left join c_cust_account a on t.cust_id = a.cust_id
    
    	and t.cust_id = #{custId,jdbcType=INTEGER}
    	and t.real_name = #{realName,jdbcType=VARCHAR}
    	and t.cert_no = #{certNo,jdbcType=VARCHAR}
    	and t.cert_area = #{certArea,jdbcType=VARCHAR}
    	and t.cust_type = #{custType,jdbcType=VARCHAR}
    	and t.already_auth = #{alreadyAuth,jdbcType=BIT}
    	
	    	and a.phone_no = #{custAccount.phoneNo,jdbcType=VARCHAR}
	    	and a.user_name = #{custAccount.userName,jdbcType=VARCHAR}
	    	and a.email = #{custAccount.email,jdbcType=VARCHAR}
    	
    
    order by t.create_time desc
