mapper.select:
select 

from u_menu_info

	and permission_code = #{permissionCode,jdbcType=VARCHAR}
	and menu_name = #{menuName,jdbcType=VARCHAR}
	and parent_menu = #{parentMenu,jdbcType=VARCHAR}
	and enabled = #{enabled,jdbcType=BIT}

mapper.insert:
insert into u_menu_info (permission_code, menu_name, menu_url, 
  parent_menu, css_style, enabled
  )
values (#{permissionCode,jdbcType=VARCHAR}, #{menuName,jdbcType=VARCHAR}, #{menuUrl,jdbcType=VARCHAR}, 
  #{parentMenu,jdbcType=VARCHAR}, #{cssStyle,jdbcType=VARCHAR}, #{enabled,jdbcType=BIT}
  )
